def find_marker(stream, window_size=4):
    i = window_size
    while i < len(stream):
        window = stream[i-window_size:i]
        if len(set(window)) == window_size:
            return i
        i = i+1
    return -1

def test_1_1():
    assert find_marker("bvwbjplbgvbhsrlpgdmjqwftvncz") == 5

def test_1_2():
    assert find_marker("nppdvjthqldpwncqszvftbrmjlhg") == 6

def test_1_3():
    assert find_marker("nznrnfrfntjfmvfwmzdfjlvtqnbhcprsg") == 10

def test_1_4():
    assert find_marker("zcfzfwzzqfrljwzlrfnpqdbhtmscgvjw") == 11

def test_2_1():
    assert find_marker("bvwbjplbgvbhsrlpgdmjqwftvncz", window_size=14) == 23

def test_2_2():
    assert find_marker("nppdvjthqldpwncqszvftbrmjlhg", window_size=14) == 23

def test_2_3():
    assert find_marker("nznrnfrfntjfmvfwmzdfjlvtqnbhcprsg", window_size=14) == 29

def test_2_4():
    assert find_marker("zcfzfwzzqfrljwzlrfnpqdbhtmscgvjw", window_size=14) == 26


if __name__ == '__main__':
    result = find_marker("")
    print(result)
    message_marker = find_marker(
        "",
        window_size = 14
    )
    print(message_marker)